import React, {useState,useEffect} from "react";
import "./boardStyle.scss";
import Square from "../Square";
const [arrayState, setArrayState]=useState(Array(9).fill(null));
const handelClick =(i)=>{
arrayState[i]="X"
setArrayState(arrayState);

}

export default function Board() {

  return (
    <div className="board">
      <div className="board__row">
        <Square num={1} onClick={()=>handelClick(1)}/>
        <Square num={2}/>
        <Square num={3}/>
      </div>
      <div className="board__row">
        <Square num={4}/>
        <Square num={5}/>
        <Square num={6}/>
      </div>
      <div className="board__row">
        <Square num={7}/>
        <Square num={8}/>
        <Square num={9}/>
      </div>
    </div>
  );
}
