import React, { useState, useEffect } from "react";
import "./boardStyle.scss";
import Square from "../Square";

export default function Board() {
  const [arrayState, setArrayState] = useState(Array(9).fill(null));
  const [isNext, setIsNext]= useState(true)

  const renderSquare = i => {
    return <Square num={arrayState[i]} onClick={() => handelClick(i)} />;
  };
  const handelClick = i => {
    const newSquares = [...arrayState];
    newSquares[i] = "X";
    setArrayState(newSquares);
  };

  return (

    <div className="board">
      <div className="board__title">Next Player is:<br/>  </div>
      <div className="board__row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}

      </div>
      <div className="board__row">
      {renderSquare(3)}
      {renderSquare(4)}
      {renderSquare(5)}
      </div>
      <div className="board__row">
      {renderSquare(6)}
      {renderSquare(7)}
      {renderSquare(8)}
      </div>
    </div>
  );
}
